import React, {useEffect, useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import { Typography } from '@material-ui/core';

// Step 2 - Include the react-fusioncharts component
import ReactFC from "react-fusioncharts";

// Step 3 - Include the fusioncharts library
import FusionCharts from "fusioncharts";

// Step 4 - Include the chart type
import Column2D from "fusioncharts/fusioncharts.charts";

// Step 5 - Include the theme as fusion
import FusionTheme from "fusioncharts/themes/fusioncharts.theme.fusion";

// Step 6 - Adding the chart and theme as dependency to the core fusioncharts
ReactFC.fcRoot(FusionCharts, Column2D, FusionTheme);

// Preparing the chart data
const chartData = [
  {
    label: "Venezuela",
    value: "290"
  },
  {
    label: "Saudi",
    value: "260"
  },
  {
    label: "Canada",
    value: "180"
  },
  {
    label: "Iran",
    value: "140"
  },
  {
    label: "Russia",
    value: "115"
  },
  {
    label: "UAE",
    value: "100"
  },
  {
    label: "US",
    value: "30"
  },
  {
    label: "China",
    value: "30"
  }
];

// import component
import GlobalData from './GlobalData';
import StateData from './StateData'

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  hColor: {
    color: "black",
    fontWeight: "bolder"
  },
}));

export default function CenteredGrid() {
  const classes = useStyles();

  const [globalData, setGlobalData] = useState();

  useEffect( ()=> {
    async function fatchData(){
      const apiResponse = await fetch("https://api.covid19api.com/world/total");
      const dataFromApi = await apiResponse.json()
      console.log(dataFromApi)
      setGlobalData(dataFromApi)
      // console.log(dataFromApi)
    }
    fatchData()
  }, [])

  return (
    <div className={classes.root}>
      <Grid container spacing={0}>
        <Grid item xs={4}>
          <Paper className={classes.paper}>
            <Typography className={classes.hColor} variant="h4" gutterBottom>
              Global Data
            </Typography>

            <GlobalData title="Totat Conform Cases" val={globalData ? globalData.TotalConfirmed : "Loading..."} color="Black"/>
            <GlobalData title="Total Death" val={globalData ? globalData.TotalDeaths : "Loading..."} color="Red"/>
            <GlobalData title="Total Recovered" val={globalData ? globalData.TotalRecovered : "Loading..."} color="Green"/>
          </Paper>
        </Grid>
        <Grid item xs={8}>
          <Paper className={classes.paper}>
            <Typography className={classes.hColor} variant="h4" gutterBottom>
              Countries Data
            </Typography>

            <StateData />
          </Paper>
        </Grid>
      </Grid>
      <Grid container spacing={0}>
        <Grid item xs={4}>

        </Grid>
        <Grid item xs={8}>
          <Paper className={classes.paper}>

          </Paper>
        </Grid>
      </Grid>


    </div>
  );
}
