import React, {useEffect, useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

// import component
import GlobalData from './GlobalData'

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
}));

export default function CenteredGrid() {
  const classes = useStyles();

  const [globalData, setGlobalData] = useState();

  useEffect( ()=> {
    async function fatchData(){
      const apiResponse = await fetch("https://api.covid19api.com/world/total");
      const dataFromApi = await apiResponse.json()
      setGlobalData(dataFromApi)
      console.log(dataFromApi)
    }
    fatchData()
  }, [])

  return (
    <div className={classes.root}>
      <Grid container spacing={3}>
        <Grid item xs={4}>

          <Typography variant="h3" gutterBottom>
            Global Data
          </Typography>

          <Paper className={classes.paper}>
              <GlobalData title="Totat Conform Casse" val={globalData ? globalData.TotalConfirmed : "Loading..."} color="Black"/>
              <GlobalData title="Total Death" val={globalData ? globalData.TotalDeaths : "Loading..."} color="Red"/>
              <GlobalData title="Total Recovered" val={globalData ? globalData.TotalRecovered : "Loading..."} color="Green"/>
          </Paper>
        </Grid>
        <Grid item xs={8}>
            <Paper className={classes.paper}>

            </Paper>
        </Grid>
      </Grid>
    </div>
  );
}
